                credentials.profile = {
                    id: profile.sub,
                    username: profile.email,
                    displayName: profile.nickname,
                    firstName: profile.given_name,
                    lastName: profile.family_name,
                    email: profile.email,
                    raw: profile
                };

                return callback();
            });
        }
    };
};

