            const query = {
                access_token: credentials.token
            };

            get(settings.uri + '/api/user.whoami', query, (profile) => {

                credentials.profile = {
                    id: profile.result.phid,
                    username: profile.result.userName,
                    displayName: profile.result.realName,
                    email: profile.result.primaryEmail,
                    raw: profile
                };

                return callback();
            });
        }
    };
};

